import React from 'react';
import {
  AppBar as MuiAppBar,
  Toolbar,
  IconButton,
  Button,
  TextField,
  Box,
  Avatar,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import logo from '../assets/FG.png';

interface AppBarProps {
  onMenuClick: () => void;
}

const AppBar: React.FC<AppBarProps> = ({ onMenuClick }) => {
  return (
    <MuiAppBar position="static">
      <Toolbar>
        <IconButton
          edge="start"
          color="inherit"
          aria-label="menu"
          onClick={onMenuClick}
        >
          <MenuIcon />
        </IconButton>
        <img src={logo} alt="Logo" style={{ height: '40px', marginRight: '20px' }} />
        
        <Box sx={{ flexGrow: 1, display: 'flex', justifyContent: 'center', gap: 2 }}>
          <Button color="inherit">Accueil</Button>
          <Button color="inherit">Films</Button>
          <Button color="inherit">Séries</Button>
          <Button color="inherit">À propos</Button>
        </Box>

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <TextField
            placeholder="Que recherchez-vous?"
            variant="outlined"
            size="small"
            InputProps={{
              startAdornment: <SearchIcon />,
            }}
            sx={{
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              borderRadius: 1,
              '& .MuiOutlinedInput-root': {
                color: 'white',
              },
            }}
          />
          <Button
            variant="contained"
            color="error"
            sx={{ color: 'white' }}
          >
            S'inscrire
          </Button>
          <IconButton color="inherit">
            <Avatar sx={{ bgcolor: 'primary.main' }} />
          </IconButton>
        </Box>
      </Toolbar>
    </MuiAppBar>
  );
};

export default AppBar;
